import React from "react";
import questions from "./components/questions";

let index = 0; // Track current question
let score = 0; // Track score

const Quiz = () => {
  // Function to handle the answer and move to the next question
  const nextQuestion = (option) => {
    if (option === questions[index].answer) {
      score++; // Increment score if the answer is correct
    }
    index++; // Move to the next question

    if (index >= questions.length) {
      alert(`Quiz Finished! Your score is: ${score}`);
      // Restart the quiz
      index = 0;
      score = 0;
    }
    render(); // Manually trigger re-render (simulation)
  };

  // Function to render the current question
  const render = () => {
    const currentQuestion = questions[index];
    const options = currentQuestion.options;

    let optionsButtons = options
      .map((option, i) => {
        return `<button 
          class="option" 
          onClick="nextQuestion('${option}')"
        >
          ${option}
        </button>`;
      })
      .join("");

    document.getElementById("quiz-container").innerHTML = `
      <h2>${currentQuestion.question}</h2>
      ${optionsButtons}
    `;
  };

  // Initialize render
  render();

  return <div id="quiz-container"></div>;
};

export default Quiz;
